import React, { useEffect, useState } from "react";
import { authAxios } from "../authaxios/auth/authaxios";
import "../App.css";
const Main = () => {
  const [userData, setUserData] = useState([]);
  const [ref, setref] = useState(true);
  const [data, setdata] = useState({
    name: "",
    email: "",
  });

  function GetData() {
    authAxios
      .get("GetData")
      .then((res) => {
        setUserData(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }

  useEffect(() => {
    GetData();
  }, [ref]);

  const handle = (e) => {
    e.preventDefault();
    console.log(data.name, data.email);

    if (data.name === "" || data.email === "") {
      alert("please fill the data");
    } else {
      let UserdataObject = {
        name: data.name,
        email: data.email,
      };

      authAxios
        .post("createData", UserdataObject)
        .then((res) => {
          console.log(res);
          setref(!ref);
        })
        .catch((err) => {
          console.log(err);
        });
      setdata({ ...data, name: "", email: "" });

      // authAxios
    }
  };

  function deletData(id) {
   
    
       authAxios.delete(`deleteData/${id}`).then((res)=>{
           console.log(res.data.msg);
           
            let userName = res.data.deleted.name
           console.log();
           alert(`${userName}`)
           setref(!ref);

       }).catch((err)=>{
          console.log(err);
          


       })
      
      
      
  }

  return (
    <div>
      <form onSubmit={handle}>
        <input
          type="text"
          placeholder="name"
          value={data.name}
          onChange={(e) => {
            setdata({ ...data, name: e.target.value });
          }}
        />
        <input
          type="text"
          placeholder="email"
          value={data.email}
          onChange={(e) => {
            setdata({ ...data, email: e.target.value });
          }}
        />
        <button>click</button>
      </form>

      <table>
        <thead>
          <th>S.no</th>
          <th>Name</th>
          <th>Email</th>
          <th>actions</th>
        </thead>
        <tbody>
          {userData &&
            userData.map((v, i) => {
              let { email, name , _id} = v;

              return (
                <tr key={i}>
                  <td>{i + 1}</td>
                  <td>{name}</td>
                  <td>{email}</td>
                  <td>
                    <button>edit</button> <button onClick={()=>{deletData(_id)}}>delete</button>
                  </td>
                </tr>
              );
            })}
        </tbody>
      </table>
    </div>
  );
};

export default Main;
